//This is the main page
import React, { useState, useRef } from 'react';
import { ThemeProvider } from 'styled-components'; //Wrapper component that uses Context API
import { GlobalStyles } from './global'; //Use GlobalStyles component created in global.js
import { theme } from './theme';
import { Burger, Menu } from './components';
import FocusLock from 'react-focus-lock';
import { useOnClickOutside } from './hooks';

function App() {
  const [open, setOpen] = useState(false);
  const node = useRef();
  const menuId = "main-menu";

  useOnClickOutside(node, () => setOpen(false));

  render()
  {
    return (
      <ThemeProvider theme={theme}>
          <GlobalStyles />
          <div ref={node}>
            <FocusLock disabled={!open}>
              <Burger open={open} setOpen={setOpen} aria-controls={menuId} />
              <Menu open={open} setOpen={setOpen} id={menuId} />
            </FocusLock>
          </div>
          <div>
            <h1>Hello. This is burger menu tutorial</h1>
            <img src="https://image.flaticon.com/icons/svg/2016/2016012.svg" alt="burger icon" />
            <small>Icon made by <a href="https://www.freepik.com/home">Freepik</a> from <a href="https://www.flaticon.com">www.flaticon.com</a></small>
          </div>

      </ThemeProvider>
    );
  };
}

export default App;
